INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ am a student.', 'I', '["I","You","He","She"]', 'Subject Pronouns', 'A1', 'Use "I" for the first person singular subject when talking about yourself.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ is my best friend. (talking about a boy)', 'He', '["I","You","He","She"]', 'Subject Pronouns', 'A1', 'Use "He" when referring to a male person.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ are from New York.', 'We', '["I","We","He","She"]', 'Subject Pronouns', 'A1', 'Use "We" for the first person plural subject.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ is a beautiful city. (talking about Paris)', 'It', '["He","She","It","They"]', 'Subject Pronouns', 'A1', 'Use "It" for things, places, animals, and ideas.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ are my parents.', 'They', '["He","She","It","They"]', 'Subject Pronouns', 'A1', 'Use "They" for plural subjects (more than one person or thing).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ happy today.', 'am', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "am" with the subject "I".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ a doctor.', 'is', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "is" with he/she/it.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ from California.', 'are', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "are" with you/we/they.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The book _____ on the table.', 'is', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "is" with singular nouns.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('We _____ students at this university.', 'are', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "are" with "we".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I have _____ apple.', 'an', '["a","an","the","-"]', 'Articles (a/an/the)', 'A1', 'Use "an" before words starting with a vowel sound.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She is _____ teacher.', 'a', '["a","an","the","-"]', 'Articles (a/an/the)', 'A1', 'Use "a" before words starting with a consonant sound.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ sun is very bright today.', 'The', '["A","An","The","-"]', 'Articles (a/an/the)', 'A1', 'Use "the" for unique things (there is only one sun).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I need _____ umbrella.', 'an', '["a","an","the","-"]', 'Articles (a/an/the)', 'A1', 'Use "an" before "umbrella" because it starts with a vowel sound.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('This is _____ best restaurant in town.', 'the', '["a","an","the","-"]', 'Articles (a/an/the)', 'A1', 'Use "the" with superlatives (best, worst, most, etc.).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ watching TV right now.', 'am', '["am","is","are","be"]', 'Present Continuous', 'A1', 'Present continuous: am/is/are + verb-ing. Use "am" with "I".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ cooking dinner.', 'is', '["am","is","are","be"]', 'Present Continuous', 'A1', 'Use "is" with he/she/it in present continuous.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ playing soccer in the park.', 'are', '["am","is","are","be"]', 'Present Continuous', 'A1', 'Use "are" with they/we/you in present continuous.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('What _____ you doing?', 'are', '["am","is","are","do"]', 'Present Continuous', 'A1', 'Use "are" with "you" in present continuous questions.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The baby _____ sleeping.', 'is', '["am","is","are","be"]', 'Present Continuous', 'A1', 'Use "is" with singular subjects like "the baby".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ is your name?', 'What', '["What","Where","When","Who"]', 'Question Words', 'A1', 'Use "What" to ask about things or information.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ do you live?', 'Where', '["What","Where","When","Who"]', 'Question Words', 'A1', 'Use "Where" to ask about places.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ is your birthday?', 'When', '["What","Where","When","Who"]', 'Question Words', 'A1', 'Use "When" to ask about time.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ is that man?', 'Who', '["What","Where","When","Who"]', 'Question Words', 'A1', 'Use "Who" to ask about people.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ are you late?', 'Why', '["What","Where","Why","Who"]', 'Question Words', 'A1', 'Use "Why" to ask about reasons.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ do you spell your name?', 'How', '["What","How","Why","Who"]', 'Question Words', 'A1', 'Use "How" to ask about manner or method.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The cat is _____ the table.', 'on', '["on","in","at","under"]', 'Basic Prepositions', 'A1', 'Use "on" for surfaces.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She is _____ the kitchen.', 'in', '["on","in","at","under"]', 'Basic Prepositions', 'A1', 'Use "in" for enclosed spaces.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I will meet you _____ the bus stop.', 'at', '["on","in","at","under"]', 'Basic Prepositions', 'A1', 'Use "at" for specific points or locations.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The dog is _____ the bed.', 'under', '["on","in","at","under"]', 'Basic Prepositions', 'A1', 'Use "under" for something below.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The picture is _____ the wall.', 'on', '["on","in","at","under"]', 'Basic Prepositions', 'A1', 'Use "on" for things attached to surfaces like walls.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ to the store yesterday.', 'walked', '["walk","walked","walking","walks"]', 'Past Simple - Regular', 'A2', 'Add "-ed" to regular verbs for past simple.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ her homework last night.', 'finished', '["finish","finished","finishing","finishes"]', 'Past Simple - Regular', 'A2', 'Add "-ed" to regular verbs for past simple.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ soccer last weekend.', 'played', '["play","played","playing","plays"]', 'Past Simple - Regular', 'A2', 'Add "-ed" to regular verbs for past simple.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('We _____ the movie together.', 'watched', '["watch","watched","watching","watches"]', 'Past Simple - Regular', 'A2', 'Add "-ed" to regular verbs for past simple.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He _____ to music all evening.', 'listened', '["listen","listened","listening","listens"]', 'Past Simple - Regular', 'A2', 'Add "-ed" to regular verbs for past simple.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ to New York last summer.', 'went', '["go","went","gone","going"]', 'Past Simple - Irregular', 'A2', 'Go ‚Üí went (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ a delicious cake.', 'made', '["make","made","making","makes"]', 'Past Simple - Irregular', 'A2', 'Make ‚Üí made (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ the answer immediately.', 'knew', '["know","knew","known","knowing"]', 'Past Simple - Irregular', 'A2', 'Know ‚Üí knew (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He _____ a new car last month.', 'bought', '["buy","bought","buying","buys"]', 'Past Simple - Irregular', 'A2', 'Buy ‚Üí bought (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('We _____ breakfast at 8 AM.', 'ate', '["eat","ate","eaten","eating"]', 'Past Simple - Irregular', 'A2', 'Eat ‚Üí ate (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ a strange noise last night.', 'heard', '["hear","heard","hearing","hears"]', 'Past Simple - Irregular', 'A2', 'Hear ‚Üí heard (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('This book is _____ than that one.', 'more interesting', '["interesting","more interesting","most interesting","interestinger"]', 'Comparatives', 'A2', 'Use "more + adjective" for long adjectives in comparatives.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('My house is _____ than yours.', 'bigger', '["big","bigger","biggest","more big"]', 'Comparatives', 'A2', 'Add "-er" to short adjectives. Double the consonant for CVC words.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She runs _____ than her brother.', 'faster', '["fast","faster","fastest","more fast"]', 'Comparatives', 'A2', 'Add "-er" to short adjectives for comparatives.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('This exam was _____ than the last one.', 'easier', '["easy","easier","easiest","more easy"]', 'Comparatives', 'A2', 'Change "y" to "i" and add "-er" for adjectives ending in "y".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Your idea is _____ than mine.', 'better', '["good","better","best","more good"]', 'Comparatives', 'A2', 'Good ‚Üí better (irregular comparative).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ you help me with this?', 'Can', '["Can","Do","Are","Have"]', 'Modal Verbs - Can/Could', 'A2', 'Use "Can" to ask for help or make requests.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ speak three languages.', 'can', '["can","cans","could","is can"]', 'Modal Verbs - Can/Could', 'A2', 'Use "can" to express ability. No "s" for third person.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('When I was young, I _____ run very fast.', 'could', '["can","could","can to","could to"]', 'Modal Verbs - Can/Could', 'A2', 'Use "could" for past ability.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ I borrow your pen?', 'Could', '["Could","Do","Am","Have"]', 'Modal Verbs - Can/Could', 'A2', 'Use "Could" for polite requests.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ swim when I was five.', 'couldn''t', '["can''t","couldn''t","don''t can","wasn''t can"]', 'Modal Verbs - Can/Could', 'A2', 'Use "couldn''t" for past inability.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ never _____ to Japan.', 'have been', '["have been","has been","was","am being"]', 'Present Perfect Simple', 'B1', 'Present perfect: have/has + past participle. Use for life experiences.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ already _____ her homework.', 'has finished', '["has finished","have finished","finished","is finishing"]', 'Present Perfect Simple', 'B1', 'Use "has" with he/she/it. "Already" goes between have/has and past participle.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ lived here for ten years.', 'have', '["have","has","are","were"]', 'Present Perfect Simple', 'B1', 'Use "have" with they/we/you/I. Use "for" with periods of time.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ you ever _____ sushi?', 'Have eaten', '["Have eaten","Has eaten","Did eat","Are eating"]', 'Present Perfect Simple', 'B1', 'Use "Have + subject + ever + past participle" for experience questions.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ just _____ my lunch.', 'have had', '["have had","has had","had","am having"]', 'Present Perfect Simple', 'B1', '"Just" indicates recent completion. Use present perfect.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If it _____, we will stay home.', 'rains', '["rains","will rain","rained","rain"]', 'First Conditional', 'B1', 'First conditional: If + present simple, will + base verb.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If you study hard, you _____ the exam.', 'will pass', '["will pass","pass","passed","passing"]', 'First Conditional', 'B1', 'Use "will + base verb" in the result clause.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ angry if you are late.', 'will be', '["will be","is","was","being"]', 'First Conditional', 'B1', 'Use "will be" for future result in first conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I _____ time, I will call you.', 'have', '["have","will have","had","having"]', 'First Conditional', 'B1', 'Use present simple after "if" in first conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('We _____ go to the beach if the weather is nice.', 'will', '["will","would","are","do"]', 'First Conditional', 'B1', 'Use "will" for the result clause in first conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I _____ rich, I would travel the world.', 'were', '["were","am","will be","would be"]', 'Second Conditional', 'B1', 'Second conditional: If + past simple, would + base verb. Use "were" for all subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If she _____ here, she would help us.', 'were', '["were","is","will be","would be"]', 'Second Conditional', 'B1', 'Use "were" (not "was") in formal second conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ buy a house if I had more money.', 'would', '["would","will","could","should"]', 'Second Conditional', 'B1', 'Use "would + base verb" in the result clause.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If they _____ harder, they would succeed.', 'worked', '["worked","work","will work","would work"]', 'Second Conditional', 'B1', 'Use past simple after "if" in second conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('What _____ you do if you won the lottery?', 'would', '["would","will","do","did"]', 'Second Conditional', 'B1', 'Use "would" in questions about hypothetical situations.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The book _____ written by Shakespeare.', 'was', '["was","is","were","be"]', 'Passive Voice - Present', 'B1', 'Passive voice: be + past participle. Use "was" for past singular.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('English _____ spoken in many countries.', 'is', '["is","are","was","be"]', 'Passive Voice - Present', 'B1', 'Use "is" for present passive with singular subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The cars _____ made in Japan.', 'are', '["is","are","was","be"]', 'Passive Voice - Present', 'B1', 'Use "are" for present passive with plural subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The letter _____ sent yesterday.', 'was', '["was","is","were","has"]', 'Passive Voice - Past', 'B1', 'Use "was" for past passive with singular subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The windows _____ cleaned every week.', 'are', '["is","are","was","be"]', 'Passive Voice - Present', 'B1', 'Use "are" for present passive with plural subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('By the time I arrived, she _____ already left.', 'had', '["had","has","have","was"]', 'Past Perfect', 'B2', 'Past perfect: had + past participle. For actions before another past action.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ never seen such a beautiful sunset before that day.', 'had', '["had","have","has","was"]', 'Past Perfect', 'B2', 'Use past perfect for experiences before a specific past time.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ finished dinner when we called.', 'had', '["had","have","has","were"]', 'Past Perfect', 'B2', 'Past perfect shows the earlier of two past events.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She realized she _____ forgotten her keys.', 'had', '["had","has","have","was"]', 'Past Perfect', 'B2', 'The forgetting happened before the realizing.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I _____ known, I would have helped.', 'had', '["had","have","has","would"]', 'Past Perfect', 'B2', 'Third conditional uses past perfect in the if-clause.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I had studied harder, I _____ passed the exam.', 'would have', '["would have","will have","had","would"]', 'Third Conditional', 'B2', 'Third conditional: If + past perfect, would have + past participle.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ come if you had invited her.', 'would have', '["would have","will have","had","would"]', 'Third Conditional', 'B2', 'Use "would have + past participle" for unreal past results.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If they _____ arrived earlier, they would have met him.', 'had', '["had","have","would","would have"]', 'Third Conditional', 'B2', 'Use past perfect after "if" in third conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ told you if I had known.', 'would have', '["would have","will have","had","would"]', 'Third Conditional', 'B2', 'Third conditional expresses regret about past situations.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If we _____ taken a taxi, we would have arrived on time.', 'had', '["had","have","would","would have"]', 'Third Conditional', 'B2', 'Past perfect in the condition clause of third conditional.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She said she _____ tired.', 'was', '["is","was","be","been"]', 'Reported Speech - Statements', 'B2', 'In reported speech, present tense becomes past tense.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He told me he _____ call me later.', 'would', '["will","would","can","could"]', 'Reported Speech - Statements', 'B2', 'In reported speech, "will" becomes "would".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They said they _____ been waiting for an hour.', 'had', '["have","had","has","were"]', 'Reported Speech - Statements', 'B2', 'Present perfect becomes past perfect in reported speech.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She asked me where I _____.', 'lived', '["live","lived","living","lives"]', 'Reported Speech - Questions', 'B2', 'In reported questions, use statement word order and past tense.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He asked if I _____ help him.', 'could', '["can","could","will","would"]', 'Reported Speech - Questions', 'B2', '"Can" becomes "could" in reported speech.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Not only _____ she intelligent, but she is also hardworking.', 'is', '["is","does","has","was"]', 'Inversion', 'C1', 'Inversion after "Not only": auxiliary + subject + main verb.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Rarely _____ I seen such dedication.', 'have', '["have","has","do","did"]', 'Inversion', 'C1', 'Inversion after negative adverbs like "rarely".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Had I known about the meeting, I _____ attended.', 'would have', '["would have","will have","had","would"]', 'Advanced Conditionals', 'C1', 'Inverted conditional: Had + subject replaces If + subject + had.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('It was John _____ broke the window.', 'who', '["who","which","what","whom"]', 'Cleft Sentences', 'C1', 'Cleft sentence: It + be + focus + who/that + rest of sentence.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('What I need _____ a vacation.', 'is', '["is","are","was","were"]', 'Cleft Sentences', 'C1', 'Pseudo-cleft sentence: What + clause + be + focus.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I suggest that he _____ more careful.', 'be', '["be","is","was","being"]', 'Subjunctive Mood', 'C1', 'Subjunctive mood: base form after verbs like suggest, recommend, insist.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('It is essential that she _____ on time.', 'arrive', '["arrive","arrives","arrived","arriving"]', 'Subjunctive Mood', 'C1', 'Use base form (no -s) in subjunctive after "It is essential that".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The doctor recommended that he _____ smoking.', 'stop', '["stop","stops","stopped","stopping"]', 'Subjunctive Mood', 'C1', 'Subjunctive uses base form regardless of subject.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I _____ you, I would accept the offer.', 'were', '["were","was","am","be"]', 'Subjunctive Mood', 'C1', 'Use "were" (subjunctive) for hypothetical situations with all subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I wish I _____ speak French fluently.', 'could', '["could","can","would","will"]', 'Subjunctive Mood', 'C1', 'Use past forms after "wish" for present wishes.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The project is still up in the _____.', 'air', '["air","sky","clouds","wind"]', 'Native-like Expressions', 'C2', '"Up in the air" means uncertain or undecided.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She has a chip on her _____.', 'shoulder', '["shoulder","arm","back","head"]', 'Native-like Expressions', 'C2', '"Chip on one''s shoulder" means holding a grudge or being easily offended.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Let''s not beat around the _____.', 'bush', '["bush","tree","garden","point"]', 'Native-like Expressions', 'C2', '"Beat around the bush" means to avoid the main topic.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He''s barking up the wrong _____.', 'tree', '["tree","path","road","way"]', 'Native-like Expressions', 'C2', '"Barking up the wrong tree" means pursuing a mistaken course.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('That''s the last _____!', 'straw', '["straw","thing","drop","bit"]', 'Native-like Expressions', 'C2', '"The last straw" is the final problem that makes a situation unbearable.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ have done it, but I chose not to.', 'could', '["could","might","would","should"]', 'Subtle Grammar Distinctions', 'C2', '"Could have" expresses past ability/possibility that wasn''t used.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ have arrived by now.', 'should', '["should","would","could","might"]', 'Subtle Grammar Distinctions', 'C2', '"Should have" expresses expectation about the past.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He _____ not have said that.', 'ought', '["ought","should","would","could"]', 'Subtle Grammar Distinctions', 'C2', '"Ought not to have" is more formal than "shouldn''t have".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The data _____ that the hypothesis is correct.', 'suggest', '["suggest","suggests","suggested","suggesting"]', 'Subtle Grammar Distinctions', 'C2', '"Data" can be treated as plural (formal) or singular (informal).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Neither the students nor the teacher _____ aware of the change.', 'was', '["was","were","is","are"]', 'Subtle Grammar Distinctions', 'C2', 'With "neither...nor", the verb agrees with the nearest subject.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('My sister _____ a teacher.', 'is', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "is" with he/she/it or singular nouns.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ you from America?', 'Are', '["Am","Is","Are","Be"]', 'Present Simple - Be', 'A1', 'Use "Are" with "you" in questions.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ a car.', 'have', '["have","has","having","had"]', 'Present Simple - Have', 'A1', 'Use "have" with I/you/we/they.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ two brothers.', 'has', '["have","has","having","had"]', 'Present Simple - Have', 'A1', 'Use "has" with he/she/it.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The children _____ in the garden.', 'are', '["am","is","are","be"]', 'Present Simple - Be', 'A1', 'Use "are" with plural subjects.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I live _____ New York.', 'in', '["in","on","at","to"]', 'Basic Prepositions', 'A1', 'Use "in" with cities and countries.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The meeting is _____ Monday.', 'on', '["in","on","at","to"]', 'Basic Prepositions', 'A1', 'Use "on" with days of the week.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I wake up _____ 7 o''clock.', 'at', '["in","on","at","to"]', 'Basic Prepositions', 'A1', 'Use "at" with specific times.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ old are you?', 'How', '["What","How","Where","When"]', 'Question Words', 'A1', 'Use "How old" to ask about age.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ many books do you have?', 'How', '["What","How","Where","Which"]', 'Question Words', 'A1', 'Use "How many" for countable nouns.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ to the gym every day.', 'goes', '["go","goes","going","went"]', 'Present Simple - Verbs', 'A2', 'Add "-es" to verbs ending in -o for he/she/it.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('They _____ TV last night.', 'watched', '["watch","watched","watching","watches"]', 'Past Simple - Regular', 'A2', 'Add "-ed" for regular past tense.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ my keys yesterday.', 'lost', '["lose","lost","losing","loses"]', 'Past Simple - Irregular', 'A2', 'Lose ‚Üí lost (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ a letter to her friend.', 'wrote', '["write","wrote","written","writing"]', 'Past Simple - Irregular', 'A2', 'Write ‚Üí wrote (irregular past tense).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('This is the _____ movie I''ve ever seen.', 'best', '["good","better","best","most good"]', 'Superlatives', 'A2', 'Good ‚Üí better ‚Üí best (irregular superlative).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Mount Everest is the _____ mountain in the world.', 'highest', '["high","higher","highest","most high"]', 'Superlatives', 'A2', 'Add "-est" for short adjective superlatives.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ go to the beach tomorrow.', 'will', '["will","would","am","do"]', 'Future with Will', 'A2', 'Use "will" for future decisions.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ going to study medicine.', 'is', '["is","are","will","does"]', 'Future with Going To', 'A2', 'Use "be going to" for planned future.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('You _____ drive without a license.', 'mustn''t', '["can''t","mustn''t","don''t have to","shouldn''t"]', 'Modal Verbs - Must/Have to', 'A2', '"Mustn''t" expresses prohibition.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ usually eat breakfast.', 'don''t', '["don''t","doesn''t","am not","haven''t"]', 'Present Simple - Verbs', 'A2', 'Use "don''t" for negative present simple with I/you/we/they.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ to Paris three times.', 'have been', '["have been","was","went","am going"]', 'Present Perfect Simple', 'B1', 'Use present perfect for life experiences.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She _____ here since 2010.', 'has lived', '["has lived","lived","lives","is living"]', 'Present Perfect Simple', 'B1', 'Use present perfect with "since" for duration.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('When I arrived, they _____ dinner.', 'were having', '["were having","had","have","are having"]', 'Past Continuous', 'B1', 'Use past continuous for actions in progress in the past.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I _____ to play tennis when I was young.', 'used', '["used","use","was used","am used"]', 'Used to / Would', 'B1', 'Use "used to" for past habits.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The movie _____ directed by Steven Spielberg.', 'was', '["was","is","has","had"]', 'Passive Voice - Past', 'B1', 'Use past passive for completed actions.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I enjoy _____ books.', 'reading', '["reading","to read","read","reads"]', 'Gerunds and Infinitives', 'B1', 'Use gerund (-ing) after "enjoy".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She decided _____ a new car.', 'to buy', '["to buy","buying","buy","bought"]', 'Gerunds and Infinitives', 'B1', 'Use infinitive (to + verb) after "decide".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The man _____ lives next door is a doctor.', 'who', '["who","which","what","whom"]', 'Relative Clauses - Defining', 'B1', 'Use "who" for people in relative clauses.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The book _____ I bought is interesting.', 'which', '["who","which","what","whom"]', 'Relative Clauses - Defining', 'B1', 'Use "which" or "that" for things.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('You _____ see a doctor about that cough.', 'should', '["should","would","could","might"]', 'Modal Verbs - Should/Ought to', 'B1', '"Should" gives advice or recommendations.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('By the time she arrived, we _____ waiting for two hours.', 'had been', '["had been","have been","were","are"]', 'Past Perfect', 'B2', 'Past perfect continuous for duration before past event.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('This time next week, I _____ on a beach.', 'will be lying', '["will be lying","will lie","am lying","lie"]', 'Future Continuous', 'B2', 'Future continuous for actions in progress at future time.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('By 2030, scientists _____ a cure for cancer.', 'will have found', '["will have found","will find","find","found"]', 'Future Perfect', 'B2', 'Future perfect for completed actions before future time.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('I wish I _____ more time to travel.', 'had', '["had","have","would have","will have"]', 'Wish/If only', 'B2', 'Use past tense after "wish" for present wishes.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If only I _____ listened to your advice!', 'had', '["had","have","would","could"]', 'Wish/If only', 'B2', 'Use past perfect after "if only" for past regrets.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She asked me where I _____ the previous day.', 'had been', '["had been","have been","was","am"]', 'Reported Speech - Questions', 'B2', 'Backshift in reported speech: was ‚Üí had been.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('My sister, _____ lives in London, is visiting next week.', 'who', '["who","which","that","whom"]', 'Relative Clauses - Non-defining', 'B2', 'Non-defining relative clauses use "who" for people (with commas).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I had known about the party, I _____ come.', 'would have', '["would have","will have","had","would"]', 'Third Conditional', 'B2', 'Third conditional: would have + past participle.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('If I were you, I _____ accepted the job.', 'would have', '["would have","will have","had","would"]', 'Mixed Conditionals', 'B2', 'Mixed conditional: past condition, past result.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The report _____ be submitted by Friday.', 'must', '["must","should","could","might"]', 'Modal Verbs - Must/Have to', 'B2', '"Must" expresses obligation or necessity.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Never _____ I seen such a beautiful sunset.', 'have', '["have","had","did","was"]', 'Inversion', 'C1', 'Inversion after negative adverbs: Never + auxiliary + subject.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Seldom _____ she complain about anything.', 'does', '["does","did","has","is"]', 'Inversion', 'C1', 'Inversion with "seldom" in present tense.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('It is vital that he _____ on time.', 'be', '["be","is","was","being"]', 'Subjunctive Mood', 'C1', 'Subjunctive: base form after "It is vital that".');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The committee insisted that the proposal _____ rejected.', 'be', '["be","is","was","being"]', 'Subjunctive Mood', 'C1', 'Subjunctive after verbs of demand/suggestion.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('What I really need _____ a vacation.', 'is', '["is","are","was","were"]', 'Cleft Sentences', 'C1', 'Pseudo-cleft: What + clause + be + focus.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('It was in Paris _____ they first met.', 'that', '["that","which","where","when"]', 'Cleft Sentences', 'C1', 'It-cleft: It + be + focus + that/who.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ I to know about the meeting, I would have attended.', 'Were', '["Were","Was","Had","Should"]', 'Advanced Conditionals', 'C1', 'Inverted conditional: Were + subject replaces If + subject + were.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('_____ he to arrive early, please inform me.', 'Should', '["Should","Would","Could","If"]', 'Advanced Conditionals', 'C1', 'Inverted conditional with "should" for unlikely events.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The data _____ to support the hypothesis.', 'appear', '["appear","appears","appeared","appearing"]', 'Advanced Tense Review', 'C1', '"Data" can be plural (formal) - verb agrees.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Little _____ he know what awaited him.', 'did', '["did","does","had","was"]', 'Inversion', 'C1', 'Inversion after "Little" for emphasis.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The situation calls _____ immediate action.', 'for', '["for","on","up","out"]', 'Advanced Phrasal Verbs', 'C2', '"Call for" means to require or demand.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She came _____ a rare book at the flea market.', 'across', '["across","over","through","about"]', 'Advanced Phrasal Verbs', 'C2', '"Come across" means to find by chance.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('His argument doesn''t hold _____.', 'water', '["water","ground","weight","fire"]', 'Native-like Expressions', 'C2', '"Hold water" means to be valid or logical.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('She''s really burning the candle at both _____.', 'ends', '["ends","sides","ways","points"]', 'Native-like Expressions', 'C2', '"Burn the candle at both ends" means overworking.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The news came as a bolt from the _____.', 'blue', '["blue","sky","dark","clear"]', 'Native-like Expressions', 'C2', '"Bolt from the blue" means unexpected news.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('He _____ have been more helpful if he''d tried.', 'couldn''t', '["couldn''t","wouldn''t","shouldn''t","mightn''t"]', 'Subtle Grammar Distinctions', 'C2', 'Ironic use: "couldn''t have been more X" = was extremely X.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The phenomenon _____ yet to be fully understood.', 'has', '["has","is","was","had"]', 'Subtle Grammar Distinctions', 'C2', '"Has yet to" = has not yet been (formal).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Be that as it _____,  we must proceed.', 'may', '["may","might","can","could"]', 'Native-like Expressions', 'C2', '"Be that as it may" = regardless of that (formal).');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('The committee, _____ members were present, voted unanimously.', 'all of whose', '["all of whose","whose all","all whose","which all"]', 'Subtle Grammar Distinctions', 'C2', '"All of whose" for possession with quantifier.');
INSERT INTO questions (questionText, correctAnswer, options, category, difficulty, explanationEnglish) VALUES ('Scarcely _____ he arrived when the meeting began.', 'had', '["had","has","did","was"]', 'Subtle Grammar Distinctions', 'C2', 'Inversion with "scarcely...when" (past perfect).');
